mapper.select:
select o.orderId oid,send, sphone,semail, sprovince,scity,receive,rphone,remail remail, rprovince,rcity,`time`, `check`,c.NameZh,og.num,g.name gname,g.goodID gid, expiry, temperature, `type`, mass, length, width, height, unit from client c,`order` o,good g,`order_good` og where c.companyCode = o.client and o.orderId = og.orderID and og.goodID = g.goodID
and o.orderId = #{orderid}
and c.NameZh like CONCAT('%',CONCAT(#{company},'%'))
and o.send like CONCAT('%',CONCAT(#{send},'%'))
and o.sphone = #{sphone}
and o.receive = CONCAT('%',CONCAT(#{receive},'%'))
and rphone = #{rphone}

select o.orderId oid,send, sphone,semail, sprovince,scity,receive,rphone,remail remail, rprovince,rcity,`time`, `check`,c.NameZh,og.num,g.name gname, g.goodID gid, expiry, temperature, `type`, mass, length, width, height, unit from client c,`order` o,good g,`order_good` og where c.companyCode = o.client and o.orderId = og.orderID and og.goodID = g.goodID and `check` = "提交"
and o.orderId = #{orderid}
and c.NameZh like CONCAT('%',CONCAT(#{company},'%'))
and o.send like CONCAT('%',CONCAT(#{send},'%'))
and o.sphone = #{sphone}
and o.receive = CONCAT('%',CONCAT(#{receive},'%'))
and rphone = #{rphone}

